        <div class=\"w-75\">
        {{ form_start(form) }}
            <div>{{form_row(form.titre)}}</div>
            <div>{{form_row(form.auteur)}}</div>
            <div>{{form_row(form.date)}}</div>
            <div>{{form_row(form.description)}}</div>
            <div>{{form_row(form.contenu)}}</div>
